#!/usr/bin/perl -w#$quoteDOC  = "HitchHikers.txt";#$quoteDOC  = "curmudgeon.txt";$quoteDOC  = "MoreQuotes.txt";##	Open a stream to the source so we can read it as a stream#	n.b. we could have used a fork but this would require us to load the whole thing into memory.if ( !open( QUOTES, "$quoteDOC") ) {	die "Can't open $quoteDOC\n";	}@file = <QUOTES>;@quote = split( /%%/, join( " ", @file ) );print "Content-type: text/html\r\n\r\n";$n = rand $#quote;$_ = $quote[$n];print;