#!/usr/bin/perl -w#$weatherSummary  = "weatherSummary.html";##	Open a stream to the source so we can read it as a stream#	n.b. we could have used a fork but this would require us to load the whole thing into memory.eval {	if ( !open( SUMMARY, "$weatherSummary") ) {		print "Content-type: text/html\n";		print "Can't open $weatherSummary\n";		die "Can't open $weatherSummary\n";		}	@file = <SUMMARY>;	print @file;	}or	print "Content-type: text/html\nNothing to report.";